tool.command:
@QUOTE_FOO@
@EXT_FOO@
#import re

## Preprocessing
mkdir mandatory_mandatoryinput &&
mkdir mandatory_optionalinput &&
mkdir inp_optional_mandatoryinput &&
${ ' '.join(["ln -s '%s' 'inp_optional_mandatoryinput/%s.%s' &&" % (_, re.sub('[^\w\-_]', '_', _.element_identifier), $gxy2omsext(_.ext)) for _ in $inp_optional_mandatoryinput if _]) }
#if $inp_mandatory_optionalinput:
  mkdir inp_mandatory_optionalinput &&
  ${ ' '.join(["ln -s '%s' 'inp_mandatory_optionalinput/%s.%s' &&" % (_, re.sub('[^\w\-_]', '_', _.element_identifier), $gxy2omsext(_.ext)) for _ in $inp_mandatory_optionalinput if _]) }
#end if
mkdir sect_mandatory_mandatoryinput &&
mkdir sect_mandatory_optionalinput &&
mkdir test_section.sect_inp_optional_mandatoryinput &&
${ ' '.join(["ln -s '%s' 'test_section.sect_inp_optional_mandatoryinput/%s.%s' &&" % (_, re.sub('[^\w\-_]', '_', _.element_identifier), $gxy2omsext(_.ext)) for _ in $test_section.sect_inp_optional_mandatoryinput if _]) }
#if $test_section.sect_inp_mandatory_optionalinput:
  mkdir test_section.sect_inp_mandatory_optionalinput &&
  ${ ' '.join(["ln -s '%s' 'test_section.sect_inp_mandatory_optionalinput/%s.%s' &&" % (_, re.sub('[^\w\-_]', '_', _.element_identifier), $gxy2omsext(_.ext)) for _ in $test_section.sect_inp_mandatory_optionalinput if _]) }
#end if

## Main program call

export PYTHONPATH='$__tool_directory__/CTDopts' &&                         
@EXECUTABLE@ -write_ctd ./ &&
python3 '$__tool_directory__/fill_ctd.py' '@EXECUTABLE@.ctd' '$args_json' &&
@EXECUTABLE@ -ini @EXECUTABLE@.ctd
-mandatory_mandatoryinput
${' '.join(["'mandatory_mandatoryinput/%s.%s'"%(re.sub('[^\w\-_]', '_', _.element_identifier), $gxy2omsext("tsv")) for _ in $mandatory_mandatoryinput if _])}
-mandatory_optionalinput
${' '.join(["'mandatory_optionalinput/%s.%s'"%(re.sub('[^\w\-_]', '_', _.element_identifier), mandatory_optionalinput_type) for _ in $mandatory_optionalinput if _])}
-inp_optional_mandatoryinput
${' '.join(["'inp_optional_mandatoryinput/%s.%s'"%(re.sub('[^\w\-_]', '_', _.element_identifier), $gxy2omsext(_.ext)) for _ in $inp_optional_mandatoryinput if _])}
#if $inp_mandatory_optionalinput:
  -inp_mandatory_optionalinput
  ${' '.join(["'inp_mandatory_optionalinput/%s.%s'"%(re.sub('[^\w\-_]', '_', _.element_identifier), $gxy2omsext(_.ext)) for _ in $inp_mandatory_optionalinput if _])}
#end if
-test_section:sect_mandatory_mandatoryinput
${' '.join(["'sect_mandatory_mandatoryinput/%s.%s'"%(re.sub('[^\w\-_]', '_', _.element_identifier), $gxy2omsext("tsv")) for _ in $sect_mandatory_mandatoryinput if _])}
-test_section:sect_mandatory_optionalinput
${' '.join(["'sect_mandatory_optionalinput/%s.%s'"%(re.sub('[^\w\-_]', '_', _.element_identifier), test_section.sect_mandatory_optionalinput_type) for _ in $sect_mandatory_optionalinput if _])}
-test_section:sect_inp_optional_mandatoryinput
${' '.join(["'test_section.sect_inp_optional_mandatoryinput/%s.%s'"%(re.sub('[^\w\-_]', '_', _.element_identifier), $gxy2omsext(_.ext)) for _ in $test_section.sect_inp_optional_mandatoryinput if _])}
#if $test_section.sect_inp_mandatory_optionalinput:
  -test_section:sect_inp_mandatory_optionalinput
  ${' '.join(["'test_section.sect_inp_mandatory_optionalinput/%s.%s'"%(re.sub('[^\w\-_]', '_', _.element_identifier), $gxy2omsext(_.ext)) for _ in $test_section.sect_inp_mandatory_optionalinput if _])}
#end if

## Postprocessing
${' '.join(["&& mv -n 'mandatory_mandatoryinput/%(id)s.%(gext)s' 'mandatory_mandatoryinput/%(id)s'"%{"id": re.sub('[^\w\-_]', '_', _.element_identifier), "gext": $gxy2omsext("tsv")} for _ in $mandatory_mandatoryinput if _])}
${' '.join(["&& mv -n 'mandatory_optionalinput/%(id)s.%(omsext)s' 'mandatory_optionalinput/%(id)s.%(gext)s'"%{"id": re.sub('[^\w\-_]', '_', _.element_identifier), "omsext":mandatory_optionalinput_type, "gext": oms2gxyext("mandatory_optionalinput_type")} for _ in $mandatory_optionalinput if _])}
${' '.join(["&& mv -n 'sect_mandatory_mandatoryinput/%(id)s.%(gext)s' 'sect_mandatory_mandatoryinput/%(id)s'"%{"id": re.sub('[^\w\-_]', '_', _.element_identifier), "gext": $gxy2omsext("tsv")} for _ in $sect_mandatory_mandatoryinput if _])}
${' '.join(["&& mv -n 'sect_mandatory_optionalinput/%(id)s.%(omsext)s' 'sect_mandatory_optionalinput/%(id)s.%(gext)s'"%{"id": re.sub('[^\w\-_]', '_', _.element_identifier), "omsext":test_section.sect_mandatory_optionalinput_type, "gext": oms2gxyext("test_section.sect_mandatory_optionalinput_type")} for _ in $sect_mandatory_optionalinput if _])}
